<?phprequire_once("config.php");$ban =#Ban table: Stores basic user information about each user that is bannedmysql_query("CREATE TABLE `".DB_PREFACE."ban` (	`id` INT NOT NULL AUTO_INCREMENT,	`date` VARCHAR(50) NULL DEFAULT NULL,	`ip` VARCHAR(256) NULL DEFAULT NULL,	`username` VARCHAR(50) NULL DEFAULT NULL,	`issuer` VARCHAR(50) NOT NULL DEFAULT 'No issuer provided.',	`reason` VARCHAR(512) NOT NULL DEFAULT 'No reason provided.',	`appealed` INT(1) NOT NULL DEFAULT '0',	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");$userblobs =#Userblobs table: Active session codes$UserSystem->DATABASE->query("CREATE TABLE `".DB_PREFACE."userblobs` (	`id` INT NOT NULL AUTO_INCREMENT,	`user` VARCHAR(100) NOT NULL,	`code` VARCHAR(512) NOT NULL,	`ip` VARCHAR(256) NOT NULL,	`action` VARCHAR(100) NOT NULL,	`date` INT NOT NULL,	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");$users =#Users table: Stores all information about users$UserSystem->DATABASE->query("CREATE TABLE `".DB_PREFACE."users` (	`id` INT NOT NULL AUTO_INCREMENT,	`username` VARCHAR(50) NOT NULL,	`firstName` VARCHAR(256) NOT NULL,	`lastName` VARCHAR(256) NOT NULL,	`password` VARCHAR(100) NOT NULL,	`oldPassword` VARCHAR(100) NOT NULL,	`passwordchanged` VARCHAR(50) NOT NULL DEFAULT '0000000000',	`salt` VARCHAR(512) NOT NULL,	`oldSalt` VARCHAR(512) NOT NULL,	`email` VARCHAR(256) NOT NULL,	`oldemail` VARCHAR(512) NOT NULL,	`emailchanged` INT NOT NULL DEFAULT '0000000000',	`phone` INT(11) NOT NULL DEFAULT '0',	`dateRegistered` INT NOT NULL,	`activated` INT(1) NOT NULL DEFAULT '0',	`title` VARCHAR(50) NOT NULL DEFAULT '',	`twoStep` INT(1) NOT NULL DEFAULT '0',	`lastLoggedIn` INT NOT NULL DEFAULT '0000000000',	`oldLastLoggedIn` INT NOT NULL DEFAULT '0000000000',	`ip` VARCHAR(256) NOT NULL DEFAULT '',	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");if (is_object($ban)) { #If the ban table was made successfully  echo DB_DATABASE.'> Bans table made successfully (`'.DB_PREFACE.'bans`)<br>';} else {  echo '<font style="color: red">'.DB_DATABASE.'> Bans table NOT made	successfully.</font><br>';}if (is_object($userblobs)) { #If the userblobs table was made successfull  echo DB_DATABASE.'> User blobs table made successfully (`'.DB_PREFACE.'userblobs`)<br>';} else {  echo '<font style="color: red">'.DB_DATABASE.'> User Blobs table NOT made	successfully.</font><br>';}if (is_object($users)) { #If the users table was made successfully  echo DB_DATABASE.'> Users table made successfully (`'.DB_PREFACE.'users`)';} else {  echo '<font style="color: red">'.DB_DATABASE.'> Users table NOT made	successfully.</font>';}