<?phprequire_once("config.php");$ban =#Ban table: Stores basic user information about each user that is bannedmysql_query("CREATE TABLE `".DB_PREFACE."ban` (	`id` INT(50) NOT NULL AUTO_INCREMENT,	`date` VARCHAR(50) NULL DEFAULT NULL,	`ip` VARCHAR(50) NULL DEFAULT NULL,	`username` VARCHAR(50) NULL DEFAULT NULL,	`issuer` VARCHAR(50) NOT NULL DEFAULT 'No issuer provided.',	`reason` VARCHAR(512) NOT NULL DEFAULT 'No reason provided.',	`appealed` VARCHAR(50) NOT NULL DEFAULT '0',	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");$userblobs =#Userblobs table: Active session codes$UserSystem->DATABASE->query("CREATE TABLE `".DB_PREFACE."userblobs` (	`id` INT(5) NOT NULL AUTO_INCREMENT,	`user` VARCHAR(100) NOT NULL,	`code` VARCHAR(256) NOT NULL,	`ip` VARCHAR(256) NOT NULL,	`action` VARCHAR(100) NOT NULL,	`date` VARCHAR(50) NOT NULL,	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");$users =#Users table: Stores all information about users$UserSystem->DATABASE->query("CREATE TABLE `".DB_PREFACE."users` (	`id` INT(255) NOT NULL AUTO_INCREMENT,	`username` VARCHAR(50) NOT NULL,	`oldusername` VARCHAR(50) NOT NULL,	`permusername` VARCHAR(50) NOT NULL,	`name_first` VARCHAR(256) NOT NULL,	`name_last` VARCHAR(256) NOT NULL,	`password` VARCHAR(100) NOT NULL,	`oldpassword` VARCHAR(100) NOT NULL,	`passwordchanged` VARCHAR(50) NOT NULL DEFAULT '0000000000',	`salt` VARCHAR(512) NOT NULL,	`oldsalt` VARCHAR(512) NOT NULL,	`email` VARCHAR(255) NOT NULL,	`phone` INT(11) NOT NULL DEFAULT '0',	`oldemail` VARCHAR(512) NOT NULL,	`emailchanged` VARCHAR(64) NOT NULL DEFAULT '0000000000',	`firstemail` VARCHAR(512) NOT NULL,	`date_registered` VARCHAR(255) NOT NULL,	`activated` INT(1) NOT NULL DEFAULT '0',	`bio` VARCHAR(510) NOT NULL DEFAULT '',	`sig` VARCHAR(100) NOT NULL DEFAULT '',	`title` VARCHAR(50) NOT NULL DEFAULT '',	`rep` INT(10) NOT NULL DEFAULT '0',	`2step` INT(1) NOT NULL DEFAULT '0',	`last_logged_in` VARCHAR(50) NOT NULL DEFAULT '0000000000',	`old_last_logged_in` VARCHAR(50) NOT NULL DEFAULT '0000000000',	`site` VARCHAR(255) NOT NULL,	`ip` VARCHAR(64) NOT NULL DEFAULT '',	PRIMARY KEY (`id`))COLLATE='latin1_swedish_ci'ENGINE=MyISAMAUTO_INCREMENT=0;");if (is_object($ban)) { #If the ban table was made successfully  echo DB_DATABASE.'> Bans table made successfully (`'.DB_PREFACE.'bans`)<br>';} else {  echo '<font style="color: red">'.DB_DATABASE.'> Bans table NOT made	successfully.</font><br>';}if (is_object($userblobs)) { #If the userblobs table was made successfull  echo DB_DATABASE.'> User blobs table made successfully (`'.DB_PREFACE.'userblobs`)<br>';} else {  echo '<font style="color: red">'.DB_DATABASE.'> User Blobs table NOT made	successfully.</font><br>';}if (is_object($users)) { #If the users table was made successfully  echo DB_DATABASE.'> Users table made successfully (`'.DB_PREFACE.'users`)';} else {  echo '<font style="color: red">'.DB_DATABASE.'> Users table NOT made	successfully.</font>';}